import React, { Component } from 'react';
import './todo.css';

class Todo extends Component {
  constructor(props){
    super(props);
    this.state = {
      todo: [],
      title: '',
      task: '',
      color: '#dddddd'
    }
  }

  handleChange = (event) => {
    const value = event.target.name

    this.setState({
      [value]: event.target.value
    });

    console.log('atualizando input')
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({
      todo: this.state.todo,
      title: this.state.title,
      task: this.state.task,
      color: this.state.color,
      // title: '',
      // task: '',
      // color: '#dddddd'
    });

    console.log('enviar')
  }

  renderTasks = () => {
    return this.state.todo.map(index => {
      return (
        <li
          key={index}
        > 
          <button
          >
          Remove
          </button>
        </li>
      )
    });
  }

  render() {
    return (
      <div className="Todo">
        <form onSubmit={this.handleSubmit}>
          <label>
            Title
            <input 
              type="text"
              name="title"
              value={this.state.title}
              onChange={this.handleChange}
            />
          </label>
          <label>
            Task
            <input 
              type="text"
              name="task"
              value={this.state.task}
              onChange={this.handleChange}
            />
          </label>
          <label>
            Color
            <input 
              type="color"
              name="color"
              value={this.state.color}
              onChange={this.handleChange}
            />
          </label>
          <button type="submit">Submit</button>
          <ul>
            {this.renderTasks()}
          </ul>
        </form>
      </div>
    );
  }
}

export default Todo;
